mapper.select:
select id, stu_name, stu_age, stu_class, stu_university, stu_number, stu_major, stu_phone, stu_score from student_info_single
id > #{maxId}
order by id asc limit 0, #{pageSize}

SELECT id FROM (
                   SELECT id, ROW_NUMBER() OVER (ORDER BY id) as row_num
                   FROM student_info_single
               ) AS ranked
WHERE row_num = #{sheetSize}
